mapper.select:
select a.* , b.username from orders a , users b where a.usersid =
b.usersid

	and a.ordercode = #{ordercode}


	and a.usersid = #{usersid}


	and a.total = #{total}


	and a.status = #{status}


	and a.addtime = #{addtime}


	and a.receiver = #{receiver}


	and a.address = #{address}


	and a.contact = #{contact}
